mapper.select:
select rownum fila, mt101a.CD_BIC_ORIGEN cdBicOrigen, mt101a.NB_CAMPO_20 nbCampo20, mt101b.NB_CAMPO_21 nbCampo21, mt101a.FH_CAMPO_30 fhCampo30, mt101a.FH_PROCESO fhProcesoA, mt101stenv.NB_ST_ENVIO_MT101 nbStEnvioMt101, mt101b.CD_CAMPO_32B_MNDA cdCampo32bMnda, mt101b.IM_CAMPO_32B_MONTO imCampo32bMonto, mt101b.NB_CAMPO_50A_OC_L1 nbCampo50aOcL1, mt101b.NB_CAMPO_50A_OC_L2 nbCampo50aOcL2, mt101b.NB_CAMPO_50A_OC_L3 nbCampo50aOcL3, mt101b.NB_CAMPO_59A_L1 nbCampo59aL1, mt101b.NB_CAMPO_57A_L1 nbCampo57aL1, mt101b.NB_CAMPO_70_L1 nbCampo70L1, mt101repte.NB_DESC_ST_CW nbDescStCw, mt101repte.FH_PROCESO fhProcesoRepte, mt101repte.NB_ARCH_CASH nbArchCash from TGM134_MT101_A mt101a, TGM135_MT101_B mt101b, TGM142_MT101_STENV mt101stenv, TGM139_MT101_REPTE mt101repte where mt101b.cd_secuencia_a = mt101a.cd_secuencia_a and mt101repte.cd_st_envio_mt101 = mt101stenv.cd_st_envio_mt101 and mt101repte.cd_secuencia_a = mt101b.cd_secuencia_a and mt101repte.cd_secuencia_b = mt101b.cd_secuencia_b
and to_date(mt101repte.FH_CAMPO_30,'dd-MM-rrrr') between to_date(#{fhProcesoInicio},'dd-MM-rrrr') and to_date(#{fhProcesoFin},'dd-MM-rrrr')
and to_date(mt101repte.FH_CAMPO_30,'dd-MM-rrrr') > to_date(#{fhCampo30T},'dd-MM-rrrr')
and to_date(mt101repte.FH_CAMPO_30,'dd-MM-rrrr') = to_date(#{fhCampo30T2},'dd-MM-rrrr')
and NB_ARCH_CASH is not null
and mt101stenv.CD_ST_ENVIO_MT101 != #{cdStEnvioMt101T}
and mt101repte.NB_ARCH_CASH = #{nbArchCash}
and mt101repte.nu_lote_envio = #{nuLoteEnvio}
and mt101stenv.CD_ST_ENVIO_MT101 = #{cdStEnvioMt101}
and mt101stenv.CD_ST_ENVIO_MT101 in
#{cdStEnvioMt101List}
AND mt101repte.nb_campo_50h = #{nbCampo50h}

	
		select NB_CAMPO_50H nbCampo50
		from tgm139_mt101_repte repte,
	  	tgm138_mt101ctrato ctrato
	  	where repte.nb_campo_50h = ctrato.cd_cuenta_cheques
	  	and repte.fh_campo_30 > sysdate
	  	and ctrato.cd_cuenta_cheques = #{cdCuentaCheques}

mapper.update:
update tgm139_mt101_repte
set cd_st_envio_mt101 = #{cdStEnvioMt101},
nb_desc_st_cw = #{nbDescStCw},
nb_st_ope_cw = #{nbStOpeCw}
where cd_repte_mt101 = #{cdRepteMt101}
