mapper.select:
SELECT BITACORA.CD_BITACORA idBitacora, BITACORA.CD_USUARIO idUsuario, USUARIO.NB_USUARIO nombreUsuario, BITACORA.NB_BITACORA nombreBitacora, BITACORA.TX_BITACORA descripcionBitacora, BITACORA.TM_BITACORA horaBitacora, BITACORA.FH_BITACORA fechaBitacora, EVENTO.NB_EVENTO_MAPEADOR nombreEventoMapeador, BITACORA.CD_EVENTO_MAPEADOR idEventoMapeador FROM TGM507_BITACORA BITACORA, TGM503_USUARIO USUARIO, TGM508_EVENTO_MAPA EVENTO WHERE BITACORA.CD_USUARIO = USUARIO.CD_USUARIO AND BITACORA.CD_EVENTO_MAPEADOR = EVENTO.CD_EVENTO_MAPEADOR AND BITACORA.CD_EVENTO_MAPEADOR in (1,2,3,4,5,6,7,8,9)
AND BITACORA.CD_EVENTO_MAPEADOR = #{idEventoMapeador}
AND BITACORA.CD_USUARIO = #{idUsuario}
AND to_char(BITACORA.TM_BITACORA,'dd-MM-rrrr HH24:MI') BETWEEN to_char(#{fechaInicio},'dd-MM-rrrr HH24:MI') AND to_char(#{fechaFin},'dd-MM-rrrr HH24:MI')
ORDER BY CD_BITACORA
