mapper.select:
SELECT CANAL.CD_CANAL idCanal, CANAL.NB_CANAL nombreCanal, CANAL.TX_CANAL descripcionCanal, CANAL.TM_ALTA fechaAlta, CANAL.TX_RUTA_CANAL_ENT rutaCanalEntrada, CANAL.TX_RUTA_CANAL_SAL rutaCanalSalida, CANAL.CD_ST_OBJETO idEstatusObjeto, OBJETO.NB_ST_OBJETO nombreEstatusObjeto FROM TGM511_CANAL CANAL, TGM534_ST_OBJETO OBJETO WHERE CANAL.CD_ST_OBJETO = OBJETO.CD_ST_OBJETO AND OBJETO.CD_ST_OBJETO != #{idBaja}
AND CANAL.NB_CANAL LIKE #{nombreCanal}
AND CANAL.TX_CANAL LIKE #{descripcionCanal}
AND OBJETO.CD_ST_OBJETO = #{idEstatusObjeto}
AND to_date(CANAL.TM_ALTA ,'dd/MM/rrrr') BETWEEN to_date(#{fechaInicio},'dd/MM/rrrr') AND to_date(#{fechaFin},'dd/MM/rrrr')
ORDER BY CANAL.NB_CANAL, CANAL.TM_ALTA, OBJETO.NB_ST_OBJETO

mapper.update:
    UPDATE TGM511_CANAL
	Set NB_CANAL = #{nombreCanal},
		TX_CANAL = #{descripcionCanal},
		CD_ST_OBJETO = #{idEstatusObjeto}
	where CD_CANAL = #{idCanal}
