mapper.select:
SELECT CLIENTE.CD_CLIENTE idCliente, CLIENTE.CD_IDENTIFICADOR idIdentificador, CLIENTE.NB_CLIENTE nombreCliente, CLIENTE.NB_CORTO_CLIENTE nombreCortoCliente, CLIENTE.TM_ALTA fechaAlta, CLIENTE.TM_MODIFICACION fechaModificacion, CLIENTE.CD_ST_OBJETO idEstatusObjeto, OBJETO.NB_ST_OBJETO nombreEstatusObjeto FROM TGM512_CLIENTE CLIENTE, TGM534_ST_OBJETO OBJETO WHERE CLIENTE.CD_ST_OBJETO = OBJETO.CD_ST_OBJETO
AND CLIENTE.CD_IDENTIFICADOR LIKE #{idIdentificador}
AND CLIENTE.NB_CLIENTE LIKE #{nombreCliente}
AND CLIENTE.NB_CLIENTE LIKE #{nombreCortoCliente}
AND CLIENTE.CD_ST_OBJETO = #{idEstatusObjeto}
ORDER BY CLIENTE.CD_CLIENTE
