mapper.select:
SELECT C.CD_PANTALLA idPantalla, C.CD_COMPONENTE idComponente, C.Nb_Componente nombreComponente, C.Cd_Tp_Componen idtipoComponente, C.Cd_Default idDefault, Tp.Nb_Tp_Componen nombreTipoComponente, P.NB_PANTALLA nombrePantalla FROM Tgm502_Componente C, Tgm501_Pantalla P, Tgm506_Tp_Componen Tp WHERE C.Cd_Pantalla = P.Cd_Pantalla AND Tp.Cd_Tp_Componen = C.Cd_Tp_Componen
AND C.CD_PANTALLA = #{idPantalla}
AND C.NB_COMPONENTE LIKE #{nombreComponente}
AND C.CD_TP_COMPONEN = #{idTipoComponente}
ORDER BY C.CD_COMPONENTE
