mapper.select:
SELECT CONTRATACION.CD_CONTRATACION idContratacion, CONTRATACION.CD_CANAL idCanal, CANAL.NB_CANAL nombreCanal, CONTRATACION.CD_CLIENTE idCliente, CLIENTE.NB_CLIENTE nombreCliente, CONTRATACION.CD_PRODUCTO idProducto, PRODUCTO.NB_PRODUCTO nombreProducto, CONTRATACION.CD_CANAL_SALIDA idCanalSalida, CANAL.NB_CANAL nombreCanalSalida, CONTRATACION.TM_ALTA fechaAlta, CONTRATACION.TM_MODIFICACION fechaModificacion, CONTRATACION.CD_ST_OBJETO idEstatusObjeto, OBJETO.NB_ST_OBJETO nombreEstatusObjeto FROM TGM519_CONTRATACION CONTRATACION, TGM511_CANAL CANAL, TGM512_CLIENTE CLIENTE, TGM515_PRODUCTO PRODUCTO, TGM534_ST_OBJETO OBJETO WHERE CONTRATACION.CD_CANAL = CANAL.CD_CANAL AND CONTRATACION.CD_CLIENTE = CLIENTE.CD_CLIENTE AND CONTRATACION.CD_PRODUCTO = PRODUCTO.CD_PRODUCTO AND CONTRATACION.CD_ST_OBJETO = OBJETO.CD_ST_OBJETO AND CONTRATACION.CD_ST_OBJETO != 5
AND CONTRATACION.CD_CANAL = #{idCanal}
AND CONTRATACION.CD_CANAL_SALIDA = #{idCanalSalida}
AND CONTRATACION.CD_CLIENTE = #{idCliente}
AND CONTRATACION.CD_PRODUCTO = #{idProducto}
AND CONTRATACION.CD_ST_OBJETO = #{idEstatusObjeto}
ORDER BY CONTRATACION.CD_CONTRATACION

mapper.update:
    UPDATE TGM519_CONTRATACION
	Set CD_ST_OBJETO = #{idEstatusObjeto}
	where CD_CONTRATACION = #{idContratacion}

mapper.select:
SELECT 
	CONTRATACION.CD_CANAL,
	CONTRATACION.CD_CLIENTE,
	CONTRATACION.CD_PRODUCTO,
	CONTRATACION.CD_CANAL_SALIDA 
FROM TGM519_CONTRATACION CONTRATACION
WHERE

	CONTRATACION.CD_CANAL = #{idCanal}
	OR  CONTRATACION.CD_CANAL_SALIDA = #{idCanal}


    CONTRATACION.CD_CLIENTE = #{idCliente}


	CONTRATACION.CD_PRODUCTO = #{idProducto}
