mapper.select:
SELECT Statusobjeto.Cd_St_Objeto idEstatusObjeto, Statusobjeto.Cd_St_Clave idEstatusClave, Statusobjeto.Nb_St_Objeto nombreEstatusObjeto, Statusobjeto.Tx_St_Objeto descripcionEstatusObjeto, Statusobjeto.Nb_Tabla nombreTabla, Statusclave.Nb_St_Clave nombreStatusClave From Tgm534_St_Objeto Statusobjeto, Tgm509_St_Clave Statusclave Where Statusobjeto.Cd_St_Clave = Statusclave.Cd_St_Clave
And Statusobjeto.Nb_Tabla = #{nombreTabla}
And Statusobjeto.Nb_St_Objeto Like #{nombreEstatusObjeto}
And Statusobjeto.Tx_St_Objeto Like #{descripcionEstatusObjeto}
and Statusobjeto.Cd_St_Clave = #{idEstatusClave}
ORDER BY Statusobjeto.Nb_Tabla

mapper.update:
Set Cd_St_Clave = #{idEstatusClave},
	Nb_St_Objeto = #{nombreEstatusObjeto},
	Nb_Tabla = #{nombreTabla},
	Tx_St_Objeto = #{descripcionEstatusObjeto}
where Cd_St_Objeto = #{idEstatusobjeto}
