mapper.select:
SELECT M.CD_MAPA_GMM idMapaGmm, M.NB_MAPA_GMM nombreMapaGmm, M.TX_MAPA_GMM descripcionMapaGmm, M.TM_ALTA fechaAlta, M.TM_MODIFICACION fechaModificacion, M.CD_ST_OBJETO idEstatusObjeto, O.NB_ST_OBJETO nombreEstatusObjeto FROM TGM514_MAPA_GMM M, TGM534_ST_OBJETO O WHERE M.CD_ST_OBJETO = O.CD_ST_OBJETO
AND M.NB_MAPA_GMM LIKE #{nombreMapaGmm}
AND M.TX_MAPA_GMM LIKE #{descripcionMapaGmm}
AND M.TM_ALTA like TO_DATE(TO_CHAR( #{fechaAlta}, 'YYYY-MM-DD'), 'YYYY-MM-DD')
AND M.TM_MODIFICACION like TO_DATE(TO_CHAR( #{fechaModificacion}, 'YYYY-MM-DD'), 'YYYY-MM-DD')
AND M.CD_ST_OBJETO = #{idEstatusObjeto}
ORDER BY M.CD_MAPA_GMM
