mapper.select:
SELECT unique perfil.TX_PERFIL AS descripcionPerfil, perfil.ST_PERFIL AS estatusPerfil, perfil.Cd_Perfil As idPerfil, perfil.Nb_Perfil As nombrebPerfil, estatus.Nb_St_Objeto As descipcionEstatus FROM Tgm504_Perfil Perfil,
Tgm501_Pantalla pantalla, Tgm505_Ctrlpermiso Permiso, Tgm502_Componente Componente,
Tgm534_St_Objeto Estatus Where Perfil.St_Perfil != 26 And Estatus.Cd_St_Objeto = Perfil.St_Perfil
and Pantalla.Cd_Pantalla = Componente.Cd_Pantalla AND Permiso.Cd_Componente = Componente.Cd_Componente And Perfil.Cd_Perfil = Permiso.Cd_Perfil And Componente.Cd_Pantalla = #{idPantalla}
and TX_PERFIL like #{descripcionPerfil}
and Nb_Perfil like #{nombrebPerfil}
and St_Perfil = #{estatusPerfil}
and perfil.Cd_Perfil != #{idPerfil}

mapper.update:
Update Tgm504_Perfil Set 
	Nb_Perfil = #{nombrebPerfil},
	Tx_Perfil = #{descripcionPerfil},
	St_Perfil = #{estatusPerfil}
where Cd_Perfil = #{idPerfil}

mapper.delete:
Delete Tgm505_Ctrlpermiso
Where Cd_Componente = #{idComponente} 
	  and Cd_Perfil = #{idPerfil}
