mapper.select:
SELECT PRODUCTO.CD_PRODUCTO idProducto, PRODUCTO.CD_FLUJO idFlujo, PRODUCTO.NB_PRODUCTO nombreProducto, PRODUCTO.TX_PRODUCTO descripcionProducto, PRODUCTO.TM_ALTA fechaAlta, PRODUCTO.TM_MODIFICACION fechaModificacion, PRODUCTO.CD_ST_OBJETO idEstatusObjeto, FLUJO.NB_FLUJO nombreFlujo, OBJETO.NB_ST_OBJETO nombreEstatusObjeto FROM TGM515_PRODUCTO PRODUCTO, TGM513_FLUJO FLUJO, TGM534_ST_OBJETO OBJETO WHERE PRODUCTO.CD_FLUJO = FLUJO.CD_FLUJO AND PRODUCTO.CD_ST_OBJETO = OBJETO.CD_ST_OBJETO AND PRODUCTO.CD_ST_OBJETO != #{idBaja}
AND PRODUCTO.CD_FLUJO = #{idFlujo}
AND PRODUCTO.NB_PRODUCTO LIKE #{nombreProducto}
AND PRODUCTO.TX_PRODUCTO LIKE #{descripcionProducto}
AND OBJETO.CD_ST_OBJETO = #{idEstatusObjeto}
ORDER BY PRODUCTO.CD_PRODUCTO

SELECT 
	PRODUCTO.CD_PRODUCTO idProducto,
	PRODUCTO.NB_PRODUCTO nombreProducto
FROM 
	TGM515_PRODUCTO PRODUCTO
WHERE PRODUCTO.CD_ST_OBJETO != #{idBaja}

	AND PRODUCTO.NB_PRODUCTO = #{nombreProducto}


	AND PRODUCTO.CD_FLUJO = #{idFlujo}

ORDER BY PRODUCTO.CD_PRODUCTO
